import React from "react";
import { TestimonialCard } from "../components/TestimonialCard";
export default function TestimonialsPage() {
  const testimonials = [
    {
      name: "Amit Sharma",
      feedback: "DMA helped us double our online sales in just 3 months. The team was responsive, professional, and results-focused."
    },
    {
      name: "Riya Mehta",
      feedback: "They redesigned our website and launched Facebook Ads — we now get consistent leads every week!"
    },
    {
      name: "Nikhil Rao",
      feedback: "Truly the best marketing team we’ve worked with. SEO results were noticeable within the first few weeks."
    }
  ];

  return (
    <div className="max-w-5xl mx-auto p-6 space-y-8 bg-cover bg-center" style={{ backgroundImage: "url('/assets/testimonials.jpg')" }}>
      <h1 className="text-4xl font-bold text-center text-blue-600">Client Testimonials</h1>
      <p className="text-center text-gray-700">
        Here’s what our happy clients have to say about working with DMA.
      </p>

      <div className="grid md:grid-cols-2 gap-6">
        {testimonials.map((t, index) => (
          <TestimonialCard key={index} name={t.name} feedback={t.feedback} />
        ))}
      </div>
    </div>
  );
}
