import React from "react";
import { motion } from "framer-motion";

export default function AboutPage() {
  const fadeInUp = {
    hidden: { opacity: 0, y: 20 },
    visible: (i = 1) => ({
      opacity: 1,
      y: 0,
      transition: {
        delay: i * 0.2,
        duration: 0.6,
        ease: "easeOut",
      },
    }),
  };

  const listItemVariant = {
    hidden: { opacity: 0, x: -20 },
    visible: (i) => ({
      opacity: 1,
      x: 0,
      transition: {
        delay: i * 0.2 + 0.4,
        duration: 0.5,
        ease: "easeOut",
      },
    }),
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8 }}
      className="w-full min-h-screen bg-scroll md:bg-fixed bg-cover bg-center bg-no-repeat"
      style={{ backgroundImage: "url('/assets/aboutus_page.jpg')" }}
    >
      {/* Overlay */}
      <div className="bg-black bg-opacity-60 min-h-screen w-full px-6 py-10">
        <div className="max-w-4xl mx-auto space-y-8 text-white">
          {/* Heading */}
          <motion.h1
            initial={{ y: -30, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.7 }}
            className="text-3xl sm:text-4xl md:text-5xl font-bold text-center uppercase underline decoration-yellow-400 decoration-4 underline-offset-4"
          >
            About Us
          </motion.h1>

          {/* Intro */}
          <motion.p
            variants={fadeInUp}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            className="text-base sm:text-lg md:text-xl text-gray-100 text-justify"
          >
            At DMA, we are passionate about helping businesses thrive in the digital world.
            Our team of creative marketers, designers, and developers work together to create
            meaningful online experiences that deliver measurable results.
          </motion.p>

          {/* Info Sections */}
          <div className="space-y-6">
            {/* Mission */}
            <motion.div
              custom={1}
              variants={fadeInUp}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <h2 className="text-2xl sm:text-3xl text-yellow-300 font-semibold">
                üåü Our Mission
              </h2>
              <p className="text-sm sm:text-base md:text-lg text-gray-100 text-justify">
                To provide data-driven digital marketing solutions that accelerate growth
                for small and medium-sized businesses worldwide.
              </p>
            </motion.div>

            {/* Vision */}
            <motion.div
              custom={2}
              variants={fadeInUp}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <h2 className="text-2xl sm:text-3xl text-yellow-300 font-semibold">
                üöÄ Our Vision
              </h2>
              <p className="text-sm sm:text-base md:text-lg text-gray-100 text-justify">
                To become a trusted global leader in digital strategy and brand transformation.
              </p>
            </motion.div>

            {/* Why Choose Us */}
            <motion.div
              custom={3}
              variants={fadeInUp}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <h2 className="text-2xl sm:text-3xl text-yellow-300 font-semibold">
                üë©‚Äçüíª Why Choose Us?
              </h2>
              <ul className="list-disc pl-6 text-sm sm:text-base md:text-lg text-gray-100 mt-2">
                {[
                  "Results-first approach",
                  "Customized marketing strategies",
                  "Dedicated team of certified professionals",
                  "Transparent reporting and performance tracking",
                ].map((item, index) => (
                  <motion.li
                    key={index}
                    custom={index}
                    variants={listItemVariant}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true }}
                  >
                    {item}
                  </motion.li>
                ))}
              </ul>
            </motion.div>
          </div>
        </div>
      </div>
    </motion.div>
  );
}
