import React, { useState } from "react";

function AuthChoicePage() {
  const [isSignup, setIsSignup] = useState(false);

  const bgGradient = isSignup
    ? "from-emerald-400 to-emerald-700"
    : "from-blue-600 to-blue-900";

  return (
    <div className={`w-screen h-screen flex items-center justify-center bg-gradient-to-b ${bgGradient} text-white`}>
      <div className="bg-white bg-opacity-10 p-10 rounded-lg shadow-lg max-w-md w-full space-y-6">
        <h2 className="text-3xl font-bold text-center">{isSignup ? "Sign Up" : "Login"}</h2>

        {/* Form Fields */}
        <div className="space-y-4">
          <input
            type="text"
            placeholder="Username"
            className="w-full p-2 rounded bg-white bg-opacity-80 text-black"
          />
          {isSignup && (
            <input
              type="email"
              placeholder="Email"
              className="w-full p-2 rounded bg-white bg-opacity-80 text-black"
            />
          )}
          <input
            type="password"
            placeholder="Password"
            className="w-full p-2 rounded bg-white bg-opacity-80 text-black"
          />
        </div>

        {/* Action Button */}
        <button className="w-full py-2 rounded bg-black text-white hover:bg-opacity-80 transition">
          {isSignup ? "Sign Up" : "Login"}
        </button>

        {/* Toggle Buttons */}
        <div className="flex justify-between pt-4">
          {!isSignup && (
            <button
              onClick={() => setIsSignup(true)}
              className="text-sm text-white underline"
            >
              Don't have an account? Sign up
            </button>
          )}
          {isSignup && (
            <button
              onClick={() => setIsSignup(false)}
              className="text-sm text-white underline"
            >
              Already have an account? Login
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default AuthChoicePage;
