import React, { useState } from "react";

function AuthChoicePage() {
  const [isSignup, setIsSignup] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    const { username, email, password } = formData;

    // Simple Validation
    if (!username || !password || (isSignup && !email)) {
      setMessage("⚠️ Please fill in all required fields.");
      return;
    }

    setMessage(isSignup ? "✅ Signed up successfully!" : "✅ Logged in successfully!");

    // Optionally reset form
    setFormData({ username: "", email: "", password: "" });
  };

  const bgGradient = isSignup
    ? "from-emerald-400 to-emerald-700"
    : "from-blue-600 to-blue-900";

  return (
    <div className={`w-screen h-screen flex items-center justify-center bg-gradient-to-b ${bgGradient} text-white`}>
      <div className="bg-white bg-opacity-10 p-10 rounded-lg shadow-lg max-w-md w-full space-y-6">
        <h2 className="text-3xl font-bold text-center">{isSignup ? "Sign Up" : "Login"}</h2>

        {message && <p className="text-sm text-yellow-300 text-center">{message}</p>}

        {/* Form Fields */}
        <div className="space-y-4">
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleInputChange}
            className="w-full p-2 rounded bg-white bg-opacity-80 text-black"
          />
          {isSignup && (
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleInputChange}
              className="w-full p-2 rounded bg-white bg-opacity-80 text-black"
            />
          )}
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleInputChange}
            className="w-full p-2 rounded bg-white bg-opacity-80 text-black"
          />
        </div>

        {/* Submit Button */}
        <button
          onClick={handleSubmit}
          className="w-full py-2 rounded bg-black text-white hover:bg-opacity-80 transition"
        >
          {isSignup ? "Sign Up" : "Login"}
        </button>

        {/* Toggle Links */}
        <div className="flex justify-between pt-4">
          {!isSignup ? (
            <button onClick={() => setIsSignup(true)} className="text-sm text-white underline">
              Don't have an account? Sign up
            </button>
          ) : (
            <button onClick={() => setIsSignup(false)} className="text-sm text-white underline">
              Already have an account? Login
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default AuthChoicePage;
